from blocks_bp import bp_init, btns, dpad, gamepad, lpad_x, lpad_y, neopixel_fill, neopixel_init, neopixel_set, neopixel_zero, rpad_x, rpad_y, servo
from pybricks.hubs import PrimeHub
from pybricks.tools import wait
from umath import atan2

# Set up all devices.
prime_hub = PrimeHub()

# Initialize variables.
controller = 'nintendo'
n_leds = 24
gpio = 23
x = 0
y = 0
theta = 0
n = 0


# The main program starts here.
bp_init('A', controller == 'nintendo')
neopixel_init(n_leds, gpio)
while True:
    gamepad()
    x = (lpad_x() + 512) / 205
    y = (lpad_y() + 512) / 205
    prime_hub.display.off()
    prime_hub.display.pixel(y, x, 100)
    n = int(atan2(lpad_x(), lpad_y()) * n_leds * -25 / 156) + 12
    print(lpad_x(), lpad_y(), n, sep=",")
    neopixel_zero()
    neopixel_set(n, 0, 100, 0)
    wait(20)
