from bluepad import bluepad_init, fill_neopixel, get_buttons, get_direction_pad, get_left_stick_horizontal, get_left_stick_vertical, get_right_stick_horizontal, get_right_stick_vertical, init_neopixel, set_neopixel, set_servo
from pybricks.parameters import Color, Direction, Port
from pybricks.pupdevices import Motor

# Set up all devices.
left_engine = Motor(Port.C, Direction.CLOCKWISE)
right_engine = Motor(Port.D, Direction.COUNTERCLOCKWISE)


# The main program starts here.
bluepad_init('F')
init_neopixel(12, 12)
while True:
    left_engine.dc(get_left_stick_vertical())
    right_engine.dc(get_right_stick_vertical())
    set_neopixel(0, Color.RED, get_right_stick_vertical() / 100)
    set_servo(1, get_right_stick_horizontal() * 0.9 + 90)
